// index.js
module.exports = {
  regex: {
    match: function(regexpStr, string) {
      var re = new RegExp(regexpStr);
      return string.match(re);
    }
  },
  clozify: function(lst, i) {
    var before = lst.slice(0, i);
    var after = lst.slice(i+1, lst.length);
    return { before: before, after: after, full: before.concat(after) };
  },
  insert: function(lst, item, index) {
    return lst.slice(0, index).concat(
        [item]
      ).concat(
        lst.slice(index, lst.length)
      );
  },
  reverse: function(lst) {
    var newlst = [];
    for (var i=1; i<(lst.length-1); i++) {
      newlst.push(lst[lst.length-i])
    }
    return newlst;
  },
  underscore: {
    without: function(lst, elem) {
      return _.without(lst, elem);
    }
  },
  range: function(a, b) {
  	var lst = [];
  	for (var i=a; i<b; i++) {
  		lst.push(i)
  	}
  	return lst;
  }
};
